import React from 'react';
import videos from '../data/videos'; // asumsi ini semua video termasuk milik user
import './Profile.css';

const Profile = () => {
    const username = '@ancha';
    const userVideos = videos.filter(v => v.user === username);

    const totalLikes = userVideos.reduce((sum, v) => sum + (v.likes || 0), 0);

    return (
        <div className="profile-page">
            <div className="profile-header">
                <img src="https://via.placeholder.com/100" alt="profile" className="profile-pic" />
                <h2>{username}</h2>
                <p>{userVideos.length} Video | ❤️ {totalLikes}</p>
            </div>

            <div className="video-grid">
                {userVideos.map(video => (
                    <video key={video.id} src={video.url} className="profile-video" controls />
                ))}
            </div>
        </div>
    );
};

export default Profile;
