import React, { useRef, useEffect, useState } from 'react';
import videos from '../data/videos';
import './VideoFeed.css';
import { FaHeart } from 'react-icons/fa';

const VideoFeed = () => {
    const videoRefs = useRef([]);
    const [likes, setLikes] = useState(videos.map(() => 0));
    const [paused, setPaused] = useState(videos.map(() => false));

    // Intersection Observer
    useEffect(() => {
        const options = {
            root: null,
            threshold: 0.9,
        };

        const observer = new IntersectionObserver((entries) => {
            entries.forEach((entry) => {
                const index = videoRefs.current.findIndex((v) => v === entry.target);
                if (entry.isIntersecting) {
                    if (!paused[index]) videoRefs.current[index].play();
                } else {
                    videoRefs.current[index].pause();
                }
            });
        }, options);

        videoRefs.current.forEach((video) => {
            if (video) observer.observe(video);
        });

        return () => {
            videoRefs.current.forEach((video) => {
                if (video) observer.unobserve(video);
            });
        };
    }, [paused]);

    const toggleLike = (index) => {
        setLikes((prev) => {
            const updated = [...prev];
            updated[index]++;
            return updated;
        });
    };

    const togglePlayPause = (index) => {
        const video = videoRefs.current[index];
        if (video.paused) {
            video.play();
        } else {
            video.pause();
        }

        setPaused((prev) => {
            const updated = [...prev];
            updated[index] = !updated[index];
            return updated;
        });
    };

    return (
        <div className="video-feed">
            {videos.map((video, index) => (
                <div key={video.id} className="video-container">
                    <video
                        ref={(el) => (videoRefs.current[index] = el)}
                        src={video.url}
                        className="video-player"
                        muted
                        loop
                        playsInline
                        onClick={() => togglePlayPause(index)}
                    />
                    <div className="overlay">
                        <h5>{video.user}</h5>
                        <p>{video.caption}</p>
                    </div>
                    <div className="like-button" onClick={() => toggleLike(index)}>
                        <FaHeart size={28} color="white" />
                        <span>{likes[index]}</span>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default VideoFeed;
